// stores/suggestions.ts
import {defineStore} from 'pinia'
import grocerySuggestions from '~/data/suggestions.json';
import {useGroceryList} from "~/composables/useGroceryList";
const { items, fetchItems } = useGroceryList();

export const useSuggestionStore = defineStore('suggestions', {
    state: () => ({
        defaultSuggestions: [] as { name: string }[],
        userSuggestions: [] as { name: string }[],
        loading: false,
    }),

    getters: {
        combinedSuggestions(state) {
            const all = [...state.defaultSuggestions, ...state.userSuggestions]
            const seen = new Set()
            return all.filter((item) => {
                const lower = item.name.toLowerCase()
                if (seen.has(lower)) return false
                seen.add(lower)
                return true
            })
        },
    },

    actions: {
        async fetchSuggestions() {
            await fetchItems();
            this.loading = true
            try {
                this.defaultSuggestions = grocerySuggestions


                const res = items.value;
                this.userSuggestions = Array.isArray(res)
                    ? res.filter((item: any) => item.checked).map((item: any) => ({ name: item.name, checked: false }))
                    : []
                // this.userSuggestions = (Array.isArray(res.data) ? res.data : []).map((item: any) => ({name: item.name}));
            } catch (error) {
                console.error('Fout bij ophalen suggesties:', error)
            } finally {
                this.loading = false
            }
        },
    },
})
<script setup lang="ts">
import {ref, computed} from 'vue';
import {useRoute} from 'vue-router';
import {useGroceryList} from '~/composables/useGroceryList';
import { useSuggestionStore } from '~/stores/suggestions'

import suggestionsData from '~/data/suggestions.json';
const {
  addItem,
  items,
  fetchItems,
  increaseItems,
  decreaseItems
} = useGroceryList();
const emit = defineEmits(['item-added', 'close']);

const suggestionStore = useSuggestionStore()

onMounted(() => {
  suggestionStore.fetchSuggestions()
})
const route = useRoute();
const listId = parseInt(route.params.id as string)

const newItem = ref('');


async function addItemToList(itemName: string) {
  const name = itemName.trim();
  if (!name) return;

  await addItem(name, listId);

  await fetchItems(listId);

  newItem.value = '';
}

const filteredSuggestions = computed(() => {
  const suggestions = suggestionStore.combinedSuggestions.filter(item =>
      item.name.toLowerCase().includes(newItem.value.toLowerCase())
  );
  const isDuplicate = suggestions.some(item => item.name.toLowerCase() === newItem.value.toLowerCase());
  return newItem.value && !isDuplicate ? [{ name: newItem.value, checked:false }, ...suggestions] : suggestions;
});

fetchItems(listId)
</script>
<template>
  <div class="min-h-screen">
    <div class="flex-auto overflow-y-auto p-4 pb-20">
      <div class="items-center flex-col">
        <input
            type="text"
            v-model="newItem"
            placeholder="Enter new item"
            class="w-full mb-3 px-4 py-3 rounded-xl border border-gray-300 focus:ring-2 focus:ring-blue-400 focus:border-blue-400 text-base sm:text-lg md:text-xl"
        />
        <ul class="space-y-3">
          <li
              v-for="item in filteredSuggestions"
              :key="item.name"
              class="flex items-center justify-between bg-white rounded-xl shadow-sm p-3 sm:p-4 md:p-5"
          >
            <div class="flex items-center justify-between w-full">
              <div class="flex items-center space-x-2">
                <button @click="addItemToList(item.name)" :class="items.some(listItem => listItem.name.toLowerCase() === item.name.toLowerCase()) ? 'text-green-500' : 'text-black'">
                  {{ items.filter(item => !item.checked).some(listItem => listItem.name.toLowerCase() === item.name.toLowerCase()) ? '‚úîÔ∏è' : '‚ûï' }}
                </button>
                <span class="text-sm sm:text-base md:text-lg">{{ item.name }}</span>
              </div>
              <div class="flex items-center space-x-2" v-if="items.filter(item => !item.checked).some(listItem => listItem.name.toLowerCase() === item.name.toLowerCase())">
                <button
                    class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                    @click="decreaseItems(items.find(listItem => listItem.name.toLowerCase() === item.name.toLowerCase()))"
                >‚àí
                </button>

                <span class="text-sm sm:text-base font-semibold min-w-[20px] sm:min-w-[24px] text-center">
                   {{ items.find(listItem => listItem.name.toLowerCase() === item.name.toLowerCase())?.quantity ?? 1 }}
                </span>

                <button
                    v-if="items.filter(item => !item.checked).some(listItem => listItem.name.toLowerCase() === item.name.toLowerCase())"
                    class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                    @click="increaseItems(items.find(listItem => listItem.name.toLowerCase() === item.name.toLowerCase()))"
                >+
                </button>
              </div>
            </div>
          </li>
        </ul>
      </div>
    </div>
    <div class="fixed bottom-0 left-0 w-full mt-6 space-y-3 p-4">
      <button
          class="w-full py-3 rounded-xl bg-gray-200 text-gray-700 font-medium text-base hover:bg-gray-300 transition"
          @click="$emit('close')"
      >
        ‚Üê Back
      </button>
    </div>
  </div>
</template>
<script setup lang="ts">
import { ref, computed } from 'vue';
import { useRoute } from 'vue-router';
import AddItemListForm from '~/components/list/AddItemListForm.vue';
import { useGroceryList } from '~/composables/useGroceryList';
import {useListStore} from "~/stores/lists";

definePageMeta({
  middleware: 'auth',
});

const listStore = useListStore();

const route = useRoute();
const listId = route.params.id as string;

// UI state
const showAddItem = ref(false);
const showCheckedItems = ref(false);
const pullToRefresh = ref(false); // New state for pull-to-refresh
const startY = ref(0); // Track the starting Y position of the touch

// Composable for grocery list logic
const {
  items,
  fetchItems,
  increaseItems,
  decreaseItems,
  clearItem,
  checked,
} = useGroceryList();

// Fetch list items on page load
await fetchItems(listId);

// Handler after new item is added
function handleItemAdded() {
  showAddItem.value = false;
  fetchItems(listId); // FIXME: Should be reactive in composable ideally
}

const uncheckedItems = computed(() => items.value.filter((item: any) => !item.checked));
const checkedItems = computed(() => items.value.filter((item: any) => item.checked));

// Pull-to-refresh logic
function handleTouchStart(event: TouchEvent) {
  startY.value = event.touches[0].clientY;
}

function handleTouchMove(event: TouchEvent) {
  const currentY = event.touches[0].clientY;
  if (currentY - startY.value > 50) {
    pullToRefresh.value = true;
  }
}

async function handleTouchEnd() {
  if (pullToRefresh.value) {
    pullToRefresh.value = false;
    await fetchItems(listId); // Refresh the list
  }
}

const startX = ref(0);

function startSwipe(event, item) {
  startX.value = event.touches[0].clientX;
  item.swipeOffset = 0;
}

function moveSwipe(event, item) {
  const currentX = event.touches[0].clientX;
  const deltaX = currentX - startX.value;

  if (deltaX < 0) {
    item.swipeOffset = deltaX; // Alleen naar links swipen
  }
}

function endSwipe(event, item) {
  if (item.swipeOffset < -window.innerWidth / 2) {
    // Als meer dan 50% van de breedte is geswiped, verwijder het item
    clearItem(item);
  } else {
    // Reset swipe
    item.swipeOffset = 0;
  }
}

async function closeAddItemListForm() {
  showAddItem.value = false;
  await fetchItems(listId);
}


const list = listStore.lists.find((list: any) => list.id == parseInt(listId));

</script>

<template>
  <div
      class="max-w-screen-xl p-4 overflow-y-auto"
      @touchstart="handleTouchStart"
      @touchmove="handleTouchMove"
      @touchend="handleTouchEnd"
  >
    <h1 class="text-2xl font-bold mb-4 text-center">üõí {{list?.name}}</h1>

    <div v-if="!showAddItem">
      <ul class="space-y-3 mb-20">
        <transition-group name="fade" tag="ul" class="space-y-3 mb-20">
          <li
              v-for="item in uncheckedItems"
              :key="item.id"
              class="flex items-center justify-between bg-white rounded-xl shadow-sm p-3 active:shadow-md transition relative overflow-hidden"
              @touchstart="startSwipe($event, item)"
              @touchmove="moveSwipe($event, item)"
              @touchend="endSwipe($event, item)"
          >
            <div
                class="absolute inset-0 bg-red-200 text-white flex items-center justify-center transition-opacity pointer-events-none"
                :style="{ opacity: item.swipeOffset < 0 ? 1 : 0 }"
            >
              Verwijderen
            </div>
            <div
                class="relative flex items-center flex-1 transition-transform"
                :style="{ transform: `translateX(${item.swipeOffset || 0}px)` }"
            >
              <input
                  type="checkbox"
                  class="h-6 w-6 text-green-600 rounded flex-shrink-0"
                  :checked="item.checked"
                  @change="checked({ ...item, checked: $event.target.checked }); item.checked = $event.target.checked"
              />
              <span
                  class="text-base font-medium break-words whitespace-normal ml-2"
                  :class="{ 'line-through text-gray-500': item.checked }"
              >
                {{ item.name }}
              </span>
            </div>
            <div class="flex items-center space-x-2">
              <button
                  class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                  @click="decreaseItems(item)"
              >
                ‚àí
              </button>

              <span class="text-sm sm:text-base font-semibold min-w-[20px] sm:min-w-[24px] text-center">
                {{ item.quantity || 1 }}
              </span>

              <button
                  class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                  @click="increaseItems(item)"
              >
                +
              </button>
            </div>
          </li>
        </transition-group>
        <p
            v-if="checkedItems.length"
            class="text-center text-gray-700 mt-4 cursor-pointer hover:underline"
            @click="showCheckedItems = !showCheckedItems"
        >
          {{ showCheckedItems ? 'Hide' : 'Show' }} checked items ({{ checkedItems.length }})
        </p>
        <ul v-if="showCheckedItems" class="space-y-3 mt-4">
          <transition-group name="fade" tag="ul" class="space-y-3 mb-20">
            <li
                v-for="item in checkedItems"
                :key="item.id"
                class="flex items-center justify-between bg-white rounded-xl shadow-sm p-3 active:shadow-md transition relative overflow-hidden"
                @touchstart="startSwipe($event, item)"
                @touchmove="moveSwipe($event, item)"
                @touchend="endSwipe($event, item)"
            >
              <div
                  class="absolute inset-0 bg-red-200 text-white flex items-center justify-center transition-opacity pointer-events-none"
                  :style="{ opacity: item.swipeOffset < 0 ? 1 : 0 }"
              >
                Verwijderen
              </div>
              <div
                  class="relative flex items-center flex-1 transition-transform"
                  :style="{ transform: `translateX(${item.swipeOffset || 0}px)` }"
              >
                <input
                    type="checkbox"
                    class="h-6 w-6 text-green-600 rounded flex-shrink-0"
                    :checked="item.checked"
                    @change="checked({ ...item, checked: $event.target.checked }); item.checked = $event.target.checked"
                />
                <span
                    class="text-base font-medium break-words whitespace-normal ml-2"
                    :class="{ 'line-through text-gray-500': item.checked }"
                >
                  {{ item.name }}
                </span>
              </div>
              <div class="flex items-center space-x-2">
                <button
                    class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                    @click="decreaseItems(item)"
                >
                  ‚àí
                </button>

                <span class="text-sm sm:text-base font-semibold min-w-[20px] sm:min-w-[24px] text-center">
                  {{ item.quantity || 1 }}
                </span>

                <button
                    class="w-8 h-8 sm:w-9 sm:h-9 flex items-center justify-center bg-gray-200 rounded-full text-base sm:text-lg font-bold hover:bg-gray-300"
                    @click="increaseItems(item)"
                >
                  +
                </button>
              </div>
            </li>
          </transition-group>
        </ul>
      </ul>
      <button
          class="fixed bottom-6 right-6 bg-blue-500 text-white w-14 h-14 flex items-center justify-center rounded-full shadow-lg hover:bg-blue-600 active:scale-95 transition"
          @click="showAddItem = true"
      >
        ‚ûï
      </button>
    </div>
    <div v-else>
      <AddItemListForm @item-added="handleItemAdded" @close="closeAddItemListForm" />
    </div>
  </div>
</template>
<style scoped>
.fade-enter-active, .fade-leave-active {
  transition: opacity 0.7s ease;
}
.fade-enter-from, .fade-leave-to {
  opacity: 0;
}
</style>
